scripts.updateNginx:
echo "location ~ \"^/_int/google-drive/(.+)\$\" {
  proxy_set_header Host \$host;
  proxy_set_header X-Real-IP \$remote_addr;
  proxy_pass http://127.0.0.1:$(node -e "console.log(require('./config/auth-server-config.json').port)")/\$1\$is_args\$args;
}" > /app/nginx/google-drive-auth-server.conf && systemctl restart nginx
